join [MoviesType] T on M.[MoviesType]=T.[MoviesTypeID] 
join [MoviesLanguage] L on M.[MoviesLanguage]=L.[MoviesLanguageID]  
join [MoviesRegion] R on M.[MoviesRegion]=R.[MoviesRegionID]
join [MoviesYears] Y on M.[MoviesYears]=Y.[MoviesYearsID]
where Y.MoviesYearsName>'2010年'and Y.MoviesYearsName<'2017年' and T.MoviesTypeName LIKE'动%'

